import React, { useEffect, useState } from 'react'
import "../App.css";
export default function Home()
{
    const [inputData, setInputData] = useState("");
    const [radio, setRadio] = useState("");
    const [actorData, setActorData] = useState([]);
    const [showData, setShowData] = useState([]);


    useEffect(() =>{
        if(radio ==='actor'){
            const actdata= async ()=>{
                const response = await fetch(`https://api.tvmaze.com/search/people?q=${inputData}`);
            }
        }
    })

    return(
        <>
            <form>
                <div>
                    <input type="radio" value="actor" name="tv" className='radioinput' />
                    <label>Actor</label>
                    <input type="radio" value="show" name="tv" className="radioinput" />
                    <label>Show</label>
                    <input type="search" className="searchbar"  placeholder={`search show and actor`} value={inputData} onChange={(e)=>{setInputData(e.target.value)}}/>
                </div>
            </form>

        </>
    )
}
